import React from "react";
import {
  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  Col,
  Button,
  FormGroup,
  Input
} from "reactstrap";
import Header from "components/Headers/Header.jsx";
import axios from "axios";
import Webcam from "react-webcam";

class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };
  }

  setRef = webcam => {
    this.webcam = webcam;
  };

  capture = () => {
    const imageSrc = this.webcam.getScreenshot();
    this.setState({ image: imageSrc });
  };

  submit() {
    const data = {
      name: this.state.name,
      userData: this.state.userData
    };

    axios
      .post("http://192.168.1.160:5000/api/v1/person/", data)
      .then(response => {
        console.log("res", response);
        const data = response.data.personId;
        window.location =
          "http://localhost:8001/admin/face-verification/" + data;
      })
      .catch(error => {
        throw error;
      });
  }

  render() {
    const instruction = this.state.instruction;
    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          <Row>
            <Col className="mb-5 mb-xl-0" xl="12">
              <Card className="shadow">
                <CardHeader className="bg-transparent">
                  <Row className="align-items-center">Identify Person</Row>
                </CardHeader>
                <CardBody>
                  <Row>
                    <Col lg="6">
                      <FormGroup>
                        <Webcam />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Button color="primary" onClick={() => this.submit()}>
                      Submit
                    </Button>
                  </Row>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}

export default Index;
