import React from "react";
import Header from "components/Headers/Header.jsx";
import Step from "./examples/Step.jsx";
import Form from "./examples/Onboardform";

class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      page: "step"
    };
  }

  choose() {
    if (this.state.page === "step") {
      return <Step />;
    } else if (this.state.page === "form") {
      return <Form />;
    }
  }

  render() {
    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          <Row>
            <Col className="mb-5 mb-xl-0" xl="12">
              <Card className="shadow">
                <CardHeader className="bg-transparent">
                  <Row className="align-items-center">choose Your option</Row>
                </CardHeader>
                <CardBody>
                  <Row>
                    <Col lg="5">
                      <Button color="primary" onClick={() => this.choose(1)}>
                        ON BOARD PERSON
                      </Button>
                    </Col>
                    <Col lg="2">
                      <div style={line}></div>
                    </Col>
                    <Col lg="5">
                      <Button color="primary" onClick={() => this.choose()}>
                        IDENTIFY PERSON
                      </Button>
                    </Col>
                  </Row>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>

        {this.choose()}
      </>
    );
  }
}

export default Index;
